import { Component, ElementRef, ViewChild} from '@angular/core';
import { SlickCarouselComponent } from 'ngx-slick-carousel';
import { SlickItemDirective } from 'ngx-slick-carousel/slick.component';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  @ViewChild('slick-Modal', { static: true }) slick-Modal: SlickCarouselComponent;
  slides = [
    { img: './../../../assets/images/carousel_slide1.jpg', alt: 'Nature slide' },
    { img: './../../../assets/images/carousel_slide2.jpg', alt: 'Animals slide' },
    { img: './../../../assets/images/carousel_slide3.jpg', alt: 'People slide' }
  ];

  slideConfig = {
    slidesToShow: 1,
    slidesToScroll: 1,
    dots: false,
    arrows: false,
    infinite: true,
  };

  currentSlide = 0;

  onSlideChange(event: any) {
    this.currentSlide = event.currentSlide;
  }

  goToSlide(index: number) {
    this.slickModal.slickGoTo(index);
  }

}
