import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  slides: string[];
  i: number;

  constructor() {
    this.i = 0;
    this.slides = [
      './assets/images/carousel_slide1.jpg',
      './assets/images/carousel_slide2.jpg',
      './assets/images/carousel_slide3.jpg',
    ];
  }
  getSlide() {
    return this.slides[this.i];
  }

  getPrev() {
    this.i == 0 ? (this.i = this.slides.length - 1) : this.i--;
  }

  getNext() {
    this.i < this.slides.length - 1 ? this.i++ : (this.i = 0);
  }
}
<div id="carouselExampleControls" class="carousel slide" data-ride="carousel">

  <div class="carousel-inner" role="listbox">
      <img src="{{ getSlide() }}" alt="slide" >
  </div>

 <button (click)='getPrev()'>Prev</button>

 <button (click)='getNext()'>Next</button>

</div>
